import{_ as e,c as a,o as t,ae as s}from"./chunks/framework.AELpvxz7.js";const c="/assets/guide/384499c1-7774-40c5-b303-3a559d7ea5c4.webp",r="/assets/guide/19c407f0-259f-4823-892e-09321a5a6379.webp",d="/assets/guide/da430b3a-557f-4c87-bc35-2b29dc89c5cc.webp",i="/assets/guide/2dfe9c36-bf47-4026-a6da-5f33f25dcb49.webp",l="/assets/guide/80026031-ebaa-4450-9282-fb6a401054ad.webp",f=JSON.parse('{"title":"书架介绍","description":"介绍书架的基本功能","frontmatter":{"title":"书架介绍","description":"介绍书架的基本功能"},"headers":[],"relativePath":"blog/bookshelf-intro.md","filePath":"blog/guide/bookshelf-intro.md"}'),n={name:"blog/bookshelf-intro.md"};function p(h,o,g,b,_,m){return t(),a("div",null,o[0]||(o[0]=[s('<h1 id="书架" tabindex="-1">书架 <a class="header-anchor" href="#书架" aria-label="Permalink to &quot;书架&quot;">​</a></h1><p>在 <strong>我的书橱</strong> 中 <code>书架</code> 是阅读器的主界面, 主要用来陈列用户收集的电子书.</p><h2 id="书架功能示意图" tabindex="-1">书架功能示意图 <a class="header-anchor" href="#书架功能示意图" aria-label="Permalink to &quot;书架功能示意图&quot;">​</a></h2><p>书架中功能主要分为以下几项:</p><ul><li><strong>分类</strong>: 电子书的分类列表, 可以点击切换分类, 也可以左右滑动书架进行分类切换.</li><li><strong>排序菜单</strong>: 设置书架电子级展示样式, 列表/二列/三列.</li><li><strong>管理菜单</strong>: 包含, 导入电子书/分类管理等菜单.</li><li><strong>搜索框</strong>: 通过书名搜索电子书, 方便查找.</li><li><strong>书架</strong>: 展示已有的电子书.</li></ul><p><img src="'+c+'" alt="书架示意图"></p><h2 id="书架操作说明" tabindex="-1">书架操作说明 <a class="header-anchor" href="#书架操作说明" aria-label="Permalink to &quot;书架操作说明&quot;">​</a></h2><h3 id="书籍排序" tabindex="-1">书籍排序 <a class="header-anchor" href="#书籍排序" aria-label="Permalink to &quot;书籍排序&quot;">​</a></h3><p>默认书架按照 <code>时间倒序</code> 排列, 最近导入的书籍排在最前面.</p><p>在书架中 <code>长按</code> + <code>拖拽</code> 电子书封面, 然后把书籍拖到目标位置后 <code>放开</code>, 即可完成排序.</p><p><img src="'+r+'" alt="书架排序"></p><h3 id="书籍分类" tabindex="-1">书籍分类 <a class="header-anchor" href="#书籍分类" aria-label="Permalink to &quot;书籍分类&quot;">​</a></h3><ol><li><p><code>长按</code> 电子书封面封面, 唤出 <code>操作菜单</code>.</p><p><img src="'+d+'" alt="唤出书架菜单"></p></li><li><p>选择 <code>分类</code> 菜单, 在分类中选择 <code>对应的分类</code> 即可修改电子书的分类, 把电子书移动到对应分类下.</p><p><img src="'+i+'" alt="选择分类"></p></li></ol><h2 id="暗格" tabindex="-1">暗格 <a class="header-anchor" href="#暗格" aria-label="Permalink to &quot;暗格&quot;">​</a></h2><p><strong>暗格</strong> 是 <code>与默认书架平行的书架</code>. 其主要的作用是: <strong>可以把不想公开的书籍存放在隐藏书架里面!</strong> 起到保护隐私的作用.</p><p>把电子书隐藏到 <code>暗格</code> 中只需要在 <code>长按菜单</code> 中选择 <code>移动至</code>, 然后选择对应的 <code>暗格</code> 即可把书籍移动到暗格中.</p><p><img src="'+l+'" alt="把书籍移动到暗格中"></p><div class="tip custom-block"><p class="custom-block-title">提示</p><p>如果有需要的话, 你可以 <code>创建多个隐藏书架</code> 也就是 <strong>暗格</strong>, 对书籍进行管理.</p><p>当然, <strong>分类</strong> 也可以实现隐藏, 达到类似的目的.</p></div>',18)]))}const k=e(n,[["render",p]]);export{f as __pageData,k as default};
