import{_ as o,c as e,o as a,ae as i}from"./chunks/framework.AELpvxz7.js";const s="/assets/guide/e496ab11-e957-4e63-bcfa-7ecd47620d34.webp",c="/assets/guide/092437bb-27e7-4382-b300-858585fdae55.webp",d="/assets/guide/a2932240-c99a-4a21-8c9e-874c60153248.webp",r="/assets/guide/22ad3c5a-9f65-4ac8-986a-d227466c3b68.webp",l="/assets/guide/589b1fb0-afce-4664-90f8-e33f1b25b5fe.jpeg",p="/assets/guide/fa23b5bd-40e4-4306-a32c-3a04e3d81f8a.webp",P=JSON.parse('{"title":"导入电子书","description":"如何导入txt小说,epub,pdf,漫画到书架中","frontmatter":{"title":"导入电子书","description":"如何导入txt小说,epub,pdf,漫画到书架中"},"headers":[],"relativePath":"blog/import-book.md","filePath":"blog/guide/import-book.md"}'),n={name:"blog/import-book.md"};function b(m,t,g,h,_,u){return a(),e("div",null,t[0]||(t[0]=[i('<h1 id="导入电子书" tabindex="-1">导入电子书 <a class="header-anchor" href="#导入电子书" aria-label="Permalink to &quot;导入电子书&quot;">​</a></h1><p>目前 <strong>我的书橱</strong> 支持的文件类型有:</p><table tabindex="0"><thead><tr><th>类型</th><th>支持的格式</th></tr></thead><tbody><tr><td>电子书</td><td>TXT, EPUB, PDF, CBZ, CBR</td></tr><tr><td>压缩包</td><td>RAR, ZIP, 7Z</td></tr><tr><td>图片</td><td>JPEG, JPG, PNG, WebP, GIF</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">其他文件类型</p><p>其他更多的电子书类型会在后续更新中逐步引入.</p></div><h2 id="菜单" tabindex="-1">菜单 <a class="header-anchor" href="#菜单" aria-label="Permalink to &quot;菜单&quot;">​</a></h2><ol><li>在 <code>书架</code> 页面点击右上角 <code>+</code> 按钮.</li><li>选择  <code>本地导入</code>  或者  <code>WiFi 传书</code> 打开对应的导入页面. <img src="'+s+'" alt="导入电子书"></li></ol><h2 id="本地导入" tabindex="-1">本地导入 <a class="header-anchor" href="#本地导入" aria-label="Permalink to &quot;本地导入&quot;">​</a></h2><p>在  <code>文件选择器</code>  中选择要导入的电子书文件. <img src="'+c+'" alt="iOS文件选择器"></p><h2 id="wifi-传书" tabindex="-1">WIFI 传书 <a class="header-anchor" href="#wifi-传书" aria-label="Permalink to &quot;WIFI 传书&quot;">​</a></h2><p><strong>WiFi 传书</strong> 是利用 WiFi 网络从其他设备把电子书发送到 <strong>我的书橱</strong>, 方便在 iPhone 或者 iPad 上进行阅读.</p><ol><li><p>开启 <code>WiFi 传书</code> 页面</p><p><img src="'+d+'" alt="WIFI传书"></p></li><li><p>在浏览器中输入 <code>链接地址</code> (链接地址因网络环境而不同), 打开上传文件页面</p><p><img src="'+r+'" alt="上传电子书到iOS中"></p><div class="warning custom-block"><p class="custom-block-title">提示</p><ol><li>可以直接把文件拖到上传区域, 也可以点击区域进行文件选取.</li><li>传书时候需要使 <code>iPhone/iPad</code> 与 <code>PC</code> 或者其他设备链接到同一个 WiFi 才可以正常工作.</li></ol></div></li></ol><h2 id="文件预处理" tabindex="-1">文件预处理 <a class="header-anchor" href="#文件预处理" aria-label="Permalink to &quot;文件预处理&quot;">​</a></h2><p>在导入书籍之前, 可以在 <strong>预处理页面</strong> 对书籍的信息进行操作, 避免一些因文件名字符问题导致的排序错误等问题.</p><ol><li><p>在  <code>选择文件</code> 或者 <code>上传文件</code>  之后会自动打开 <strong>预处理页面</strong>. 该页面列出了 <strong>文件夹/压缩文件</strong> 内的文件列表.</p><div class="warning custom-block"><p class="custom-block-title">章节排序</p><p>一般来说, 算法已经对根据文件名称对章节进行了排序. 但在部分情况下排序效果可能不太理想.</p><p>你可以通过 <code>长按章节</code> + <code>拖拽至正确位置</code> 的方式对章节进行排序.</p></div><p><img src="'+l+'" alt="选中的文件列表"></p></li><li><p>例如像 <strong>漫画</strong> 这样的通过文件夹来进行章节整理的电子书类型, 可以通过 <strong>点击文件夹</strong> 进入章节编辑页面.</p><div class="warning custom-block"><p class="custom-block-title">内容排序</p><p>一般来说, 算法已经对根据文件名称对章节内部图片进行了排序. 但在部分情况下排序效果可能不太理想.</p><p>你可以通过 <code>长按文件</code> + <code>拖拽至正确位置</code> 的方式对文件进行自定义排序.</p></div><p><img src="'+p+'" alt="文件夹内的文件"></p></li></ol>',14)]))}const k=o(n,[["render",b]]);export{P as __pageData,k as default};
